    text: 'Food',
    day: 'Imorgon',
    reminder: true, 
    },
    { 
    id:2,
    text : 'tja',
    day: 'NOW',
    reminder: false,
    }

  ])

// Delete task
const deleteTask = (id) => {

  setTasks(tasks.filter((task) => task.id!==id))
}

//Toggle reminder

const toggleReminder = (id) => {
  console.log(id);
}


  return (

    <div className='container'>

      <Header/>
      {tasks.length > 0 ? (
      <Tasks tasks={tasks}  
      onDelete={deleteTask}
